import react from "react";
import AppRoutes from "./routes";
// app.js
const apiUrl = 'http://localhost:8080';

// Handle login
let token = null;

// Registration handler
document.getElementById('register-form').addEventListener('submit', async (e) => {
  e.preventDefault();
  const username = document.getElementById('register-username').value;
  const password = document.getElementById('register-password').value;

  try {
      const response = await fetch(`${apiUrl}/api/auth/register`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ username, password }),
      });

      const data = await response.text();

      if (response.ok) {
          alert(`Registration successful: ${data}`);
      } else {
          alert(`Registration failed: ${data}`);
      }
  } catch (error) {
      console.error('Error:', error);
      alert('An error occurred during registration.');
  }
});

document.getElementById('login-form').addEventListener('submit', async (e) => {
  e.preventDefault();
  const username = document.getElementById('login-username').value;
  const password = document.getElementById('login-password').value;

  try {
      const response = await fetch(`${apiUrl}/api/auth/login`, {
          method: 'POST', // Use POST for secure login
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ username, password }), // JSON payload with credentials
      });

      if (response.ok) {
          const data = await response.json();
          token = data.token; // Assuming the response contains a "token" field
          alert('Login successful');
          document.getElementById('main_page-form').addEventListener('submit', async(e) =>{
            e.preventDefault();

          })
      } else {
          const errorText = await response.text();
          console.error(`Login failed: ${errorText}`);
          alert('Login failed: ' + errorText);
      }
  } catch (error) {
      console.error('Error:', error);
      alert('An error occurred during login.');
  }
});





export default App;
