// src/pages/LoginPage.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const apiUrl = 'http://localhost:8080';

function togglePassword(){
    let password = document.getElementById("passwordInput");
    if(password.type === "password"){
        password.type = "text";
    }else{
        password.type = "password";
    }
}

function LoginPage() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {        
        e.preventDefault();
        try {
            const response = await fetch(`${apiUrl}/api/auth/login`, {
                method: 'POST',
                headers: { 'Content-type': 'application/json' },
                body: JSON.stringify({ username, password }),
            });
            if(response.ok) {
                const data = await response.text();
                alert('Successful login');
                navigate('/HomePage');
            } else if(response.status === 401) {
                toast.error('Incorrect Password. Please try again.'); // Fixed typo here
            } else {
                const textError = await response.text();
                toast.error(`Login failed: ${textError}`);
            }
        } catch (e) {
            console.error("Error", e);
            toast.error('Error occurred logging in');
        }
    };

    return (
        <div>
            <h2>Login Here</h2>
            <form onSubmit={handleLogin}>
                <input 
                    type="text"
                    placeholder="Username (e.g example123)"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input 
                    type="password"
                    placeholder="Password (e.g password@123)"
                    value={password}
                    id="passwordInput"
                    onChange={(e) => setPassword(e.target.value)}
                />
                <input
                    type="checkbox"
                    onClick={togglePassword}
                /> Show Password
                <button type="submit">Login</button>
            </form>
            <ToastContainer position='top-center' /> {/* Add this to render Toast messages */}
        </div>
    );
}

export default LoginPage;
