// src/pages/HomePage.js
import React from "react";
import homePageIMG from '../assets/images/HomePage.png';
import product1 from '../assets/images/product1.jpeg';
import product2 from '../assets/images/product2.jpeg';
import product3 from '../assets/images/product3.jpeg';
import product4 from '../assets/images/product4.jpeg';
import "../assets/styles/HomePage.css";

function HomePage() {
    return (
        <div className="home-container">
          {/* Top Banner */}
          <div className="top-banner">
            FREE SHIPPING FOR ALL U.S. ORDERS OVER $75
          </div>

          {/* Navbar */}
          <header className="navbar">
            <div className="logo">TEZKAR</div>
            <nav className="nav-links">
              <a href="#for-him">FOR HIM</a>
              <a href="#for-her">FOR HER</a>
              <a href="#new-drop">NEW DROP</a>
            </nav>
            <div className="nav-icons">
              <span>QUESTIONS? (978) 810-7418</span>
              <a href="#account" className="icon-link">ðŸ‘¤</a>
              <a href="#cart" className="icon-link">ðŸ›’</a>
            </div>
          </header>

          {/* Main Product Grid */}
          <div className="product-grid">
            <ProductCard
              image={product1}
              title="5144 - Tezkar Tracksuit Jacket"
              price="$60.00"
              colors={['black', 'red', 'blue']}
            />
            <ProductCard
              image={product2}
              title="2135 - Tezkar Tracksuit Pants"
              price="$60.00"
              colors={['black', 'red', 'blue']}
            />
            <ProductCard
              image={product3}
              title="4204 - Tezkar Fighter Tees"
              price="$42.00"
              colors={['black', 'green', 'pink']}
            />
            <ProductCard
              image={product4}
              title="4207 - Tezkar Jersey"
              price="$42.00"
              colors={['yellow', 'black', 'red']}
              soldOut
            />
          </div>

          {/* Footer */}
          <footer className="footer">
            This website uses cookies. We use cookies to personalize content and ads, to provide social media features and to analyze our traffic.
            <button className="cookie-button">Accept</button>
            <button className="cookie-button">Preferences</button>
          </footer>
        </div>
      );
    }

    const ProductCard = ({ image, title, price, colors, soldOut }) => (
      <div className="product-card">
        {soldOut && <div className="sold-out">SOLD OUT</div>}
        <img src={image} alt={title} className="product-image" />
        <h3 className="product-title">{title}</h3>
        <p className="product-price">{price}</p>
        <div className="color-dots">
          {colors.map(color => (
            <span key={color} className="color-dot" style={{ backgroundColor: color }}></span>
          ))}
        </div>
      </div>
    );

export default HomePage;
